import React from "react";
import Link from "next/link";
import Page from "../component/page";
import { getGameId } from "../api/firebase";
import { useRouter } from "next/router";

interface Props {
  game: Game;
}

export default function Home(props: Props) {
  const { game } = props;
  const router = useRouter();
  const [isJoiningGame, setIsJoiningGame] = React.useState<boolean>(false);
  const [isCreatingGame, setIsCreatingGame] = React.useState<boolean>(false);
  const [name, setName] = React.useState<string>("");
  const [gameIDToJoin, setGameIDToJoin] = React.useState<string>("");

  function handleJoinGame(e) {
    e.preventDefault();

    getGameId(gameIDToJoin)
      .then(gameId => {
        router.push(`/game/${gameId}`);
      })
      .catch(console.error);
  }

  function handleCreateGame(e) {
    e.preventDefault();
    router.push(`/setup?name=${name}`);
  }

  return (
    <Page>
      <main className="flex flex-col flex-1 items-center justify-center text-white">
        <h1 className="chalk font-bold text-6xl">darts.now.sh</h1>
        <div className="flex:col md:flex-col mt-5 pb-24">
          <Link href="/new">
            <a className="mr-4 bg-teal-500 hover:bg-teal-700 text-white font-bold py-2 px-4 rounded-lg shadow">
              Start A Game
            </a>
          </Link>
          <Link href="/join">
            <a className="bg-blue-600 hover:bg-blue-800 text-white font-bold py-2 px-4 rounded-lg shadow">
              Join Someones Game
            </a>
          </Link>
        </div>
      </main>
    </Page>
  );
}
