import React from "react";
import { useGetUserID } from "../effects/user";
import { updateScore } from "../api/firebase";

function ScoreRow(props) {
  const { number, score, gameID, playerID } = props;
  const userID = useGetUserID();
  const isMine = playerID === userID;

  function handleUpdateScore(newScore) {
    updateScore(gameID, userID, number, newScore);
  }

  return (
    <div className="score__item flex flex-no-wrap items-center p-5 relative">
      <span className="">{score || 0}</span>
      {isMine && (
        <button
          onClick={() => handleUpdateScore(score + 1)}
          className="absolute top-0 right-0 left-0 bottom-0 bg-gray-200 hover:bg-gray-300 text-gray-800 font-bold  rounded-l"
        >
          +
        </button>
      )}
    </div>
  );
}

export default function Game(props) {
  const { join_id, players, id: gameID } = props;

  return (
    <div className="mt-10 py-10 px-5 bg-teal-700 chalk text-white rounded">
      <div className="flex">
        <div className="score__column flex flex-col justify-center align-center">
          {["", 15, 16, 17, 18, 19, 20, "bull"].map(value => {
            return (
              <div className="score__item pr-2 flex items-center justify-center">
                <span>{value}</span>
              </div>
            );
          })}
        </div>
        {players.map(({ id, name, score }) => {
          return (
            <div className="score__column">
              <div className="score__item text-center border-gray-200 p-5">
                {name}
              </div>
              {Object.keys(score).map(number => {
                const scoreForNumber = score[number];
                return (
                  <ScoreRow
                    key={number}
                    number={number}
                    score={scoreForNumber}
                    playerID={id}
                    gameID={gameID}
                  />
                );
              })}
            </div>
          );
        })}
      </div>
    </div>
  );
}
