import Link from "next/link";
import { useRouter } from "next/router";
import { useGetGame } from "../../effects/game";
import { useGetUserID } from "../../effects/user";
import { addPlayerToGame } from "../../api/firebase";

interface Props {
  game?: Game;
}

export default function Game(props: Props) {
  const router = useRouter();
  const { id } = router.query;
  const [game] = useGetGame(id);
  const userID = useGetUserID();
  const creatorID = game && game.creator_id;
  const players = game && game.players;
  const amIncludedInListOfPlayers =
    players && players.includes((player: Player) => player.id === userID);

  React.useEffect(() => {
    if (!amIncludedInListOfPlayers && creatorID !== userID) {
      addPlayerToGame(gameID, userID);
    }
  }, [amIncludedInListOfPlayers, creatorID, userID]);

  return (
    <div>
      <main>
        {game && (
          <div>
            <div>ID: {game.join_id}</div>
            <div>Players</div>
            <ol>
              {players.map(player => (
                <li key={player.id}>{player.id}</li>
              ))}
            </ol>
          </div>
        )}
      </main>
    </div>
  );
}
