import { v4 as uuidv4 } from "uuid";
const USER_ID = "user_id";

interface User {
  id: string;
}

export function useGetUser(): User {
  const [id, setId] = React.useState();
  const [user, setUser] = React.useState();

  React.useEffect(() => {
    const cookies = cookie.parse(document.cookie);
    const userIDCookie = cookies[USER_ID];
    if (cookies.user_id) {
      setId(cookies.user_id);
    } else {
      const newId = uuidv4();
      setCookie(USER_ID, newId, {
        expires: new Date(new Date().getTime() + 365 * 86400000)
      });
      setId(newId);
    }
  }, []);

  React.useEffect(() => {
    if (id) {
    }
  }, [id]);

  return user;
}
