import React from "react";
import Link from "next/link";
import { useRouter } from "next/router";
import { useGetGame } from "../effects/game";

export default function Header() {
  const router = useRouter();
  const { id } = router.query;
  const [game] = useGetGame(id);
  const joinID = game && game.join_id;

  return (
    <nav className="chalk flex items-center justify-between flex-wrap p-6 border">
      <span className="font-semibold text-2xl text-indigo-900 hover:text-indigo-400 tracking-tight">
        <Link href="/">
          <a>Darts... Right Now</a>
        </Link>
      </span>
      {joinID && (
        <span className="font-semibold text-2xl text-white bg-green-800 p-2 rounded tracking-tight">
          <span>#</span>
          <span>{joinID}</span>
        </span>
      )}
    </nav>
  );
}
