import React from "react";
import Page from "../component/page";
import { useRouter } from "next/router";

interface Props {}

export default function NewGame(props: Props) {
  const router = useRouter();
  const [name, setName] = React.useState<string>("");

  function handleCreateGame(e) {
    e.preventDefault();
    router.push(`/setup?name=${name}`);
  }

  return (
    <Page>
      <main className=" flex flex-1 items-center justify-center text-white">
        <div>
          <form onSubmit={handleCreateGame}>
            <label className="text-white">Your Name</label>
            <input
              autoComplete="off"
              autoFocus
              className="block shadow appearance-none border h-40 text-6xl rounded py-2 px-3 text-gray-700 placeholder-gray-400 leading-tight focus:outline-none focus:shadow-outline"
              id="username"
              type="text"
              placeholder="Kenny Powers"
              value={name}
              onChange={e => setName(e.target.value)}
            />
            <div className="flex mt-5">
              <button
                type="submit"
                className="mr-5 bg-white hover:bg-gray-200 text-blue-900 font-bold py-2 px-4 border-b-4 border-gray-300 hover:border-gray-400 rounded"
              >
                Create Game
              </button>
              <button className="mr-5 bg-white hover:bg-gray-200 text-blue-900 font-bold py-2 px-4 border-b-4 border-gray-300 hover:border-gray-400 rounded">
                Cancel
              </button>
            </div>
          </form>
        </div>
      </main>
    </Page>
  );
}
