import React from "react";
import { v4 as uuidv4 } from "uuid";
import cookie from "cookie";
import { getUser } from "../api/firebase";

const USER_ID = "user_id";

export function useGetUser(): User {
  const [userId, setUserId] = React.useState<string>();
  const [user, setUser] = React.useState<User>();

  React.useEffect(() => {
    const cookies = cookie.serialize(document.cookie);
    const userIDCookie = cookies[USER_ID];
    if (cookies.user_id) {
      setUserId(cookies.user_id);
    } else {
      const newId = uuidv4();
      cookie.setCookie(USER_ID, newId, {
        expires: new Date(new Date().getTime() + 365 * 86400000)
      });
      setUserId(newId);
    }
  }, []);

  React.useEffect(() => {
    if (userId) {
      getUser(userId).then(userFromDB => setUser(userFromDB));
    }
  }, [userId]);

  return user;
}
