import { v4 as uuidv4 } from "uuid";
import * as firebase from "firebase/app";
import "firebase/database";

const config = {
  apiKey: "AIzaSyDp01-0TwxRjNC05CuDcpauXRyLSMv0RRw",
  authDomain: "darts-yeslab.firebaseapp.com",
  databaseURL: "https://darts-yeslab.firebaseio.com",
  projectId: "darts-yeslab",
  storageBucket: "darts-yeslab.appspot.com",
  messagingSenderId: "426404952698",
  appId: "1:426404952698:web:738ac9ab1342a1177419c3",
  measurementId: "G-7DHLMBZXEN"
};

if (!firebase.apps.length) {
  firebase.initializeApp(config);
}

const db = firebase.database();

// DB types
declare global {
  interface User {
    id: string;
    created_at: number;
  }

  interface Options {
    name: string;
  }

  interface Player {
    id: string;
    name: string;
  }

  interface Game {
    id: string;
    creator_id: string;
    join_id: string;
    players: Array<Player>;
  }

  interface Error {
    message: string;
  }
}

export function getUser(userID: string): Promise<User> {
  return db
    .ref("/users/" + userID)
    .once("value")
    .then(function(snapshot) {
      const user = snapshot.val();
      if (user) {
        return { ...user, id: userID };
      }

      return createUser(userID);
    });
}

function createUser(userID): Promise<User> {
  const user = {
    created_at: Date.now()
  };

  return new Promise((resolve, reject) => {
    db.ref("users/" + userID).set(user, error => {
      if (error) {
        reject(error);
      }

      resolve({ ...user, id: userID });
    });
  });
}

export function getGame(
  gameID: string,
  userID: string,
  options: Options,
  onUpdate: any
): Promise<Game> {
  return new Promise((resolve, reject) => {
    db.ref("/games/" + gameID).on("value", snapshot => {
      const game = snapshot.val();
      onUpdate(game);
    });
  });
}

export function getGameId(join_id: string): Promise<string> {
  return new Promise((resolve, reject) => {
    db.ref("games")
      .orderByChild("join_id")
      .equalTo(join_id)
      .on("value", function(snapshot) {
        snapshot.forEach(function(data) {
          const id = data.key;
          if (!id) {
            reject("Game not found.");
          } else {
            resolve(id);
          }
        });
      });
  });
}

function createGame(userID, name): Promise<Game> {
  const gameID = uuidv4();

  const game = {
    creator_id: userID,
    join_id: gameID.slice(0, 4),
    players: [
      {
        id: userID,
        name
      }
    ]
  };

  return new Promise((resolve, reject) => {
    db.ref("games/" + gameID).set(game, error => {
      if (error) {
        reject(error);
      } else {
        resolve({ ...game, id: gameID });
      }
    });
  });
}

export function addPlayerToGame(gameID, userID, name) {
  return new Promise((resolve, reject) => {
    db.ref("games/" + gameID)
      .once("value")
      .then(snapshot => {
        const game = snapshot.val();

        if (!game.players.some(player => player.id === userID)) {
          const newGame = {
            ...game,
            players: game.players.concat({ id: userID, name })
          };

          db.ref("games/" + gameID).update(newGame, error => {
            if (error) {
              console.error("error", error);
            }
          });
        }
      });
  });
}

