import React from "react";
import { useGetUserID } from "../effects/user";

function ScoreRow(props) {
  const { number, score, isMine } = props;
  return (
    <div className="score__item">
      {score}{" "}
      {isMine ? (
        <React.Fragment>
          <button>+</button>
          <button>-</button>
        </React.Fragment>
      ) : null}
    </div>
  );
}

export default function Game(props) {
  const { join_id, players } = props;
  const userID = useGetUserID();

  return (
    <div>
      <div>ID: {join_id}</div>

      <div className="flex">
        <div className="mr-10">
          {["", 15, 16, 17, 18, 19, 20, "bull"].map(value => {
            return <div className="score__item">{value}</div>;
          })}
        </div>
        {players.map(({ id, name, score }) => {
          const isMine = id === userID;

          {
            Object.keys(score).map(number => {
              const scoreForNumber = score[number];
              return (
                <ScoreRow
                  number={number}
                  score={scoreForNumber}
                  isMine={isMine}
                />
              );
            });
          }
          <div key={id} className="mr-10">
            <div className="score__item">{name}</div>
            <div className="score__item">
              {score[15] || 0} {isMine ? "+/-" : null}
            </div>
            <div className="score__item">
              {score[16] || 0} {isMine ? "+/-" : null}
            </div>
            <div className="score__item">
              {score[17] || 0} {isMine ? "+/-" : null}
            </div>
            <div className="score__item">
              {score[18] || 0} {isMine ? "+/-" : null}
            </div>
            <div className="score__item">
              {score[19] || 0} {isMine ? "+/-" : null}
            </div>
            <div className="score__item">
              {score[20] || 0} {isMine ? "+/-" : null}
            </div>
            <div className="score__item">
              {score["bull"] || 0} {isMine ? "+/-" : null}
            </div>
          </div>;
        })}
      </div>
    </div>
  );
}
