import React from "react";
import Page from "../component/page";
import { getGameId } from "../api/firebase";
import { useRouter } from "next/router";

interface Props {
  game: Game;
}

export default function Home(props: Props) {
  const { game } = props;
  const router = useRouter();
  const [isJoiningGame, setIsJoiningGame] = React.useState<boolean>(true);
  const [isCreatingGame, setIsCreatingGame] = React.useState<boolean>(false);
  const [name, setName] = React.useState<string>("");
  const [gameIDToJoin, setGameIDToJoin] = React.useState<string>("");

  function handleJoinGame(e) {
    e.preventDefault();

    getGameId(gameIDToJoin)
      .then(gameId => {
        router.push(`/game/${gameId}`);
      })
      .catch(console.error);
  }

  function handleCreateGame(e) {
    e.preventDefault();
    router.push(`/setup?name=${name}`);
  }

  return (
    <Page>
      <main className="flex flex-1 items-center justify-center">
        {!isJoiningGame && !isCreatingGame && (
          <div className="">
            <h1 className="font-bold text-6xl">Darts... Right Now</h1>
            <div className="flex mt-5">
              <button
                onClick={() => setIsCreatingGame(true)}
                className="mr-10 bg-teal-500 hover:bg-teal-600 text-white font-bold py-2 px-4 border-b-4 border-teal-700 hover:border-teal-500 rounded"
              >
                Start A Game
              </button>
              <button
                onClick={() => setIsJoiningGame(true)}
                className="bg-orange-400 hover:bg-orange-300 text-white font-bold py-2 px-4 border-b-4 border-orange-500 hover:border-orange-500 rounded"
              >
                Join Someones Game
              </button>
            </div>
          </div>
        )}

        {isJoiningGame && (
          <div>
            <form onSubmit={handleJoinGame}>
              <label className="text-gray-500">ID to join</label>
              <input
                autoComplete="off"
                className="block shadow appearance-none border h-40 text-6xl rounded py-2 px-3 text-gray-700 placeholder-gray-400 leading-tight focus:outline-none focus:shadow-outline"
                type="text"
                placeholder="4b90"
                value={gameIDToJoin}
                onChange={e => setGameIDToJoin(e.target.value)}
              />

              <div className="flex mt-5">
                <button
                  type="submit"
                  className="mr-5 bg-indigo-600 hover:bg-indigo-500 text-white font-bold py-2 px-4 border-b-4 border-indigo-800 hover:border-indigo-600 rounded"
                >
                  Join Game
                </button>
                <button
                  onClick={() => setIsJoiningGame(false)}
                  className="bg-gray-500 hover:bg-gray-400 text-white font-bold py-2 px-4 border-b-4 border-gray-600 hover:border-gray-500 rounded"
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        )}

        {isCreatingGame && (
          <div>
            <form onSubmit={handleCreateGame}>
              <label className="text-gray-500">Your Name</label>
              <input
                autoComplete="off"
                autoFocus
                className="block shadow appearance-none border h-40 text-6xl rounded py-2 px-3 text-gray-700 placeholder-gray-400 leading-tight focus:outline-none focus:shadow-outline"
                id="username"
                type="text"
                placeholder="Kenny Powers"
                value={name}
                onChange={e => setName(e.target.value)}
              />
              <div className="flex mt-5">
                <button
                  type="submit"
                  className="mr-5 bg-teal-500 hover:bg-teal-600 text-white font-bold py-2 px-4 border-b-4 border-teal-700 hover:border-teal-500 rounded"
                >
                  Create Game
                </button>
                <button
                  onClick={() => setIsCreatingGame(false)}
                  className="bg-gray-500 hover:bg-blue-600 text-white font-bold py-2 px-4 border-b-4 border-blue-700 hover:border-blue-500 rounded"
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        )}
      </main>
    </Page>
  );
}
