import * as firebase from "firebase";
import "firebase/database";

const config = {
  apiKey: "AIzaSyDp01-0TwxRjNC05CuDcpauXRyLSMv0RRw",
  authDomain: "darts-yeslab.firebaseapp.com",
  databaseURL: "https://darts-yeslab.firebaseio.com",
  projectId: "darts-yeslab",
  storageBucket: "darts-yeslab.appspot.com",
  messagingSenderId: "426404952698",
  appId: "1:426404952698:web:738ac9ab1342a1177419c3",
  measurementId: "G-7DHLMBZXEN"
};

if (!firebase.apps.length) {
  firebase.initializeApp(config);
  firebase.database();
}

// DB types
declare global {
  interface User {
    id: string;
  }

  interface Game {
    creator_id: string;
  }

  interface Error {
    message: string;
  }
}

export function getUser(userID: string): Promise<User> {
  return firebase
    .database()
    .ref("/users/" + userID)
    .once("value")
    .then(function(snapshot) {
      const user = snapshot.val();
      if (user) {
        return user;
      }

      return createUser(userID);
    });
}

function createUser(userID): Promise<User> {
  const user = {};

  return new Promise((resolve, reject) => {
    firebase
      .database()
      .ref("users/" + userID)
      .set(user, error => {
        if (error) {
          reject(error);
        }

        resolve({ ...user, id: userID });
      });
  });
}

export function getGame(gameID: string): Promise<Game> {
  return firebase
    .database()
    .ref("/games/" + gameID)
    .once("value")
    .then(function(snapshot) {
      const game = snapshot.val();
      if (game) {
        return game;
      }

      return createUser(gameID);
    });
}

function createGame(gameID, userId): Promise<Game> {
  const game = {
    creator_id: userId
  };

  return new Promise((resolve, reject) => {
    firebase
      .database()
      .ref("games/" + gameID)
      .set(game, error => {
        if (error) {
          reject(error);
        }

        resolve({ ...game, id: gameID });
      });
  });
}

